<template>	<div class="detail">		<div class="fixedApp">前往App</div>		<div class="groupImg">			<detail-banner></detail-banner>		</div>		<!-- 商品基本信息 -->		<goods-info></goods-info>		<!-- 该商品已经拼团情况 -->		<group-info></group-info>		<!-- 评价模块 -->		<detail-review></detail-review>		<!-- 该商品所在商家信息 -->		<goods-store></goods-store>		<!-- 商品其他详情信息 -->		<goods-detail></goods-detail>		<div class="fixedBottom">			<operate-bottom @buyClick="buyType"></operate-bottom>		</div>		<goods-spec :isShowSpec="showSpec" @reviseSpec="specHide"></goods-spec>	</div></template>	<script>		import DetailBanner from '@/components/goods/detailBanner'	import GoodsInfo from '@/components/goods/goodsInfo'	import GroupInfo from '@/components/goods/groupInfo'	import DetailReview from '@/components/goods/detailReview'	import GoodsStore from '@/components/goods/goodsStore'	import GoodsDetail from '@/components/goods/goodsDetail'	import OperateBottom from '@/components/operateBottom'	import GoodsSpec from '@/components/goods/GoodsSpec'	export default {		data() {			return {				scrollTop:'',				showSpec:false,			}		},		components:{ DetailBanner,GoodsInfo,GroupInfo,DetailReview,GoodsStore,GoodsDetail,OperateBottom,GoodsSpec },		mounted() {			console.log(123)			// console.log(window.scrollY)			window.addEventListener('scrollTop',this.scroll())		},		methods:{			scroll() {        		console.log(333)				this.scrollTop = window.pageYOffset || document.documentElement.scrollTop || document.body.scrollTop;        		console.log(this.scrollTop)			},			// 父传子，控制spec的隐藏显示			buyType(val) {				this.showSpec = true			},			specHide(val) {				this.showSpec = val			}		}	}</script><style lang="scss" scoped>	.detail {		margin-bottom:120px;	}	.fixedApp {		position:fixed;		top:40px;		right:0;		padding: 10px 30px;		border-radius:8px;		background-color:#ff7500;		color:#fff;	}	</style>